function initMap(){var e={lat:53.258692,lng:-9.078932},t=new google.maps.Map(document.getElementById("map"),{zoom:16,center:e});new google.maps.Marker({position:e,map:t})}function startTime(){var e=new Date,t=e.getHours(),a=e.getMinutes(),n=e.getSeconds(),o=e.getDate(),a=e.getMonth(),m=e.getFullYear(),r=["Jan","Feb","Mar","Apr","May","Jun","Jul","Aug","Sep","Oct","Nov","Dec"];a=checkTime(a),n=checkTime(n),document.getElementById("time").innerHTML=o+"-"+r[a]+"-"+m+" "+t+":"+a+":"+n;setTimeout(startTime,500)}function checkTime(e){return 10>e&&(e="0"+e),e}function enableContactUsFormSubmit(){hideElementById("thankYouMessage");var e=document.getElementsByTagName("form")[1];e.onsubmit=function(e){return e.preventDefault(),handleContactUsFormSubmit(),!1}}function enableTourBookingFormSubmit(){hideElementById("thankYouMessage");var e=document.getElementsByTagName("form")[1];e.onsubmit=function(e){return e.preventDefault(),handleTourBookingFormSubmit(),!1}}function enableRoomFormSubmit(){hideElementById("thankYouMessage");var e=document.getElementsByTagName("form")[1];e.onsubmit=function(e){return e.preventDefault(),handleRoomFormSubmit(),!1}}function enableTestimonialFormSubmit(){hideElementById("thankYouMessage");var e=document.getElementsByTagName("form")[1];e.onsubmit=function(e){return e.preventDefault(),handleTestimonialFormSubmit(),!1}}function hideElementById(e){var t=document.getElementById(e);isVisible(t)&&(t.style.display="none")}function showElementById(e){var t=document.getElementById(e);isVisible(t)||(t.style.display="block")}function isVisible(e){return"none"!=e.style.display}function handleTourBookingFormSubmit(){hideElementById("booking-form"),hideElementById("formDesc"),displayTourThankYouMessage()}function handleContactUsFormSubmit(){hideElementById("contact-form"),displayThankYouMessage()}function handleRoomFormSubmit(){hideElementById("room-reservation-form"),hideElementById("formDesc"),displayRoomThankYouMessage()}function handleTestimonialFormSubmit(){hideElementById("testimonial-form"),displayTestimonialThankYouMessage()}function displayThankYouMessage(){writeThankYouMessage(),showElementById("thankYouMessage")}function displayTourThankYouMessage(){writeTourThankYouMessage(),showElementById("thankYouMessage")}function displayRoomThankYouMessage(){writeRoomThankYouMessage(),showElementById("thankYouMessage")}function displayTestimonialThankYouMessage(){writeTestimonialThankYouMessage(),showElementById("thankYouMessage")}function writeThankYouMessage(){getFormData();var e=document.getElementById("senderName"),t=document.getElementById("senderSubject");e.innerHTML=messageData.name,t.innerHTML=messageData.subject}function writeTourThankYouMessage(){getTourFormData();var e=document.getElementById("senderName"),t=document.getElementById("senderTour");e.innerHTML=tourData.name,t.innerHTML=tourData.tour}function writeRoomThankYouMessage(){getRoomFormData();var e=document.getElementById("senderName"),t=document.getElementById("senderRoom");e.innerHTML=tourData.name,t.innerHTML=tourData.room}function writeTestimonialThankYouMessage(){getTestimonialFormData();var e=document.getElementById("senderName");e.innerHTML=tourData.name}function getFormData(){messageData.name=getFormElementDataById("name"),messageData.email=getFormElementDataById("email"),messageData.subject=getFormElementDataById("subject"),messageData.message=getFormElementDataById("message")}function getTourFormData(){tourData.name=getFormElementDataById("name"),tourData.tour=getFormElementDataById("tour"),tourData.tourdate=getFormElementDataById("tourdate"),tourData.arrdate=getFormElementDataById("arrdate")}function getRoomFormData(){tourData.name=getFormElementDataById("name"),tourData.room=getFormElementDataById("room"),tourData.checkin=getFormElementDataById("checkin"),tourData.checkout=getFormElementDataById("checkout")}function getTestimonialFormData(){testimonialData.name=getFormElementDataById("name"),testimonialData.email=getFormElementDataById("email"),testimonialData.city=getFormElementDataById("city"),testimonialData.message=getFormElementDataById("message")}function getFormElementDataById(e){console.log("elementId: ",e);var t,a=document.getElementById(e);switch(console.log("element: ",a),a.nodeName.toLowerCase()){case"input":case"textarea":case"date":t=a.value;case"email":t=a.value;break;case"select":t=a.options[a.selectedIndex].text}return t}var messageData={name:"",email:"",country:"",subject:"",message:""},testimonialData={name:"",email:"",city:"",message:""},tourData={name:"",tour:"",tourdate:"",arrdate:""},roomData={name:"",email:"",room:"",checkin:"",checkout:""};